import React, { useEffect } from "react";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import DashboardServices from '../../services/DashboardServices'
import Carousel from "react-elastic-carousel"
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import {
    ProductsContainer,
    ProductsWrapper,
    ProductsWrapperCar,
    ProductsContainerCar,
    ProductsCard,
    ProductsIcon,
    ProductsH1,
    ProductsH2,
    ProductsP,
} from "./ProductElement";
import { height } from "@mui/system";


function Products() {
    const navigate = useNavigate();
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true)
    const getData = () => {
        DashboardServices.getAllProducts().then(response => {
            console.log(response.data)
            const modifiedData = response.data.map(item => ({
                productId: item.productId,
                productName: item.productName,
                productImage: generateImage(item.productImage),
                productDescription: item.productDescription
            }));
            setData(modifiedData);
            setLoading(e => !e)
        }).catch(error => {
            console.log(error)
        })
    }

    const  generateImage =(base64Image)=>{
        const imageData = base64Image.split(",")[1];
  const decodedImage = atob(imageData);
  const arrayBuffer = new Uint8Array(decodedImage.length);
  for (let i = 0; i < decodedImage.length; i++) {
    arrayBuffer[i] = decodedImage.charCodeAt(i);
  }
  const blob = new Blob([arrayBuffer], { type: 'image/jpeg' });
  return URL.createObjectURL(blob);
    }

    useEffect(() => {
        getData();
    }, []);



    const handleClick = (event, param) => {
        console.log("dddddddd", event)
        console.log("pppp", param)
        navigate(`/createcontract/${param}`);
    };


    return (
        <>
            <ProductsContainer id="products">
                <ProductsH1>SPONSORED PRODUCTS </ProductsH1>
                {loading
                    ? <div style={{
                        margin: "auto",
                        top: "50%",
                        display: "block",
                    }}>
                        <Box sx={{ display: 'flex' }}>
                            <CircularProgress />
                        </Box>
                    </div>
                    :
                    <ProductsContainerCar id="products">
                        <Carousel style={{ height: "100%" }} itemsToShow={3}>
                            {
                                data.map((pe, i) => {
                                    return (
                                        <ProductsCard onClick={event => handleClick(event, pe.productId)}>
                                            <ProductsIcon src={pe.productImage} />
                                            <ProductsH2>{pe.productName}</ProductsH2>
                                            <ProductsP>{pe.productDescription}</ProductsP>
                                        </ProductsCard>
                                    );
                                })
                            }
                        </Carousel>
                    </ProductsContainerCar>
                }
            </ProductsContainer>
        </>
    );
};

export default Products;
