import React, {useState} from "react";
import smartcontractservices from "../../services/smartcontractservices";
import axiosInstance from "../../services/smartcontractservices"

function UploadFile() {

  const [file, setFile] = useState()


  function handleChange(event) {
    setFile(event.target.files[0])
  }

  function handleSubmit(event) {
    event.preventDefault()
    const url = 'http://tmsmu-2066051547.us-east-1.elb.amazonaws.com/tm/uploadfile';
    const formData = new FormData();
    formData.append('file', file);
    formData.append('fileName', file.name);
    const config = {
      headers: {
        'content-type': 'multipart/form-data',
      },
    };
    console.log(formData)
    smartcontractservices.uploadfile(url,formData,config).then(response => {
        console.log(response.data)
        alert("Congratulations" + response.data)
      }).catch(error => {
        console.log(error)
        alert("Oops we are unable to create the product")
      })

  }


  return (
        <form onSubmit={handleSubmit}>
          <h1> File Upload For Template Parameters</h1>
          <input type="file" onChange={handleChange}/>
          <button type="submit">Upload</button>
        </form>
  );
}

export default UploadFile;
