// LoginScreen.js

import React, { useState, useContext} from 'react';
import { useNavigate } from "react-router-dom";
import { GlobalStateContext } from '../../GlobalStateContext';
import './LoginScreen.css'; // Importing the CSS file
import { Container, Form, Button, Row, Col, Modal } from 'react-bootstrap';
import LoginServices from "../../services/LoginServices"
export default function LoginComponent() {
  const [state, dispatch] = useContext(GlobalStateContext);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [otp, setOtp]=useState('')
  const [showOtpModal, setShowOtpModal] = useState(false);

  const handleLogin =  async(event) => {
   event.preventDefault(  )
      const response =  await LoginServices.login(username, password).then(resp =>{
        if(resp.data.message ==='Enter OTP generated' && resp.status === 200){
          console.log("rrrr",resp)
          setIsLoggedIn(true);
          setShowOtpModal(true);
          dispatch({ type: 'SET_USER_DATA', payload: resp.data });
        }   
        else{
          setIsLoggedIn(false);
        } 
      }).catch(err =>{
        console.log(err)
      })

  }
  const handleOtpSubmit = async(event) => {
    event.preventDefault()
    // Logic to handle OTP submission
    console.log("sssss", state.userData.userId)
    const otpResponse = await LoginServices.verifyOtp(state.userData.userId,otp).then(
      resp =>{
        console.log("rrrr", resp)
        setShowOtpModal(false);
        navigate("/dashboard");
      }
    ).catch(err =>{
        console.log(err)
      })

  };

  return (
    <Container className="d-flex align-items-center justify-content-center " style={{ minHeight: "100vh" }}>
      <div className="w-100" style={{ maxWidth: "400px" }}>
        <Form>
          <h2 className="text-center mb-4">Login</h2>
          <Form.Group controlId="formBasicEmail">
            <Form.Label>Username</Form.Label>
            <Form.Control type="text" placeholder="Enter username"
              value={username}
              onChange={(e) => setUsername(e.target.value)} />
          </Form.Group>

          <Form.Group controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)} />
          </Form.Group>

          <Button variant="primary" type="submit" className="w-100 mt-4" onClick={handleLogin}>
            Login
          </Button>

          <div className="w-100 text-center mt-3">
            <a href="#">Forgot Password?</a>
          </div>
        </Form>

        <Row className="py-4">
          <Col>
            <div className="w-100 text-center">
              Need an account? <a href="#">Sign Up</a>
            </div>
          </Col>
        </Row>
      </div>
      <Modal show={showOtpModal} onHide={() => setShowOtpModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Enter OTP</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>OTP</Form.Label>
              <Form.Control type="text" placeholder="4-digit OTP" 
               value={otp}
               onChange={(e) => setOtp(e.target.value)}/>
            </Form.Group>
            <Button variant="primary" type="button" onClick={handleOtpSubmit}>
              Submit
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </Container>
  );
};
