import React, { useState } from "react";
import Navbar2 from "../../components/Navbar/navbar2";
import TM from "../../common/assets/form.svg";
import "./Features.css";
import { useEffect } from "react";
import { useParams } from "react-router-dom";
import glassclassificationservices from "../../services/glassclassificationservices";
import {
  HeroBtnWrapper,
  ArrowForward,
  ArrowRight,
} from "../../components/HeroSection/HeroElements";
import { Button } from "../../components/ButtonElement";
import CircularProgress from "@mui/material/CircularProgress";
import Box from "@mui/material/Box";
import {
  FeaturesRow,
  FeaturesColumn,
  TextWrapper,
  Img,
  TopLine,
  FeaturesSec,
  FeaturesContainer,
  ImgWrapper,
  Table,
  FeatureWrap,
} from "./Features.elements";
import Collapse from "rc-collapse";
var Panel = Collapse.Panel;
require("rc-collapse/assets/index.css");

const CreateNewContract = ({ lightTopLine }) => {
  const params = useParams();
  const [hover, setHover] = useState(false);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [dynamickeys, setdyanmickeys] = useState([]);
  console.log("param details", params["param"]);
  const onHover = () => {
    setHover(!hover);
  };
  // const dynamicdata = document.getElementById("dropdown");

  // const assetdata = {
  //   Loan: "",
  //   CreditCard: "",
  // };
  // const liabilitydata = {
  //   Savings: "",
  //   Checkings: "",
  //   Current: "",
  // };

  // const dyndata = () => {
  //   if (params["param"] === "asset") {
  //     for (let key in assetdata) {
  //       let option = document.createElement("option");
  //       option.setAttribute("value", data[key]);
  //       let optionText = document.createTextNode(key);
  //       option.appendChild(optionText);
  //       dynamicdata.appendChild(option);
  //     }
  //   }
  //   if (params["param"] === "liability") {
  //     for (let key in liabilitydata) {
  //       let option = document.createElement("option");
  //       option.setAttribute("value", data[key]);
  //       let optionText = document.createTextNode(key);
  //       option.appendChild(optionText);
  //       dynamicdata.appendChild(option);
  //     }
  //   }
  // };

  const getData = () => {
    glassclassificationservices
      .getPairPlots()
      .then((response) => {
        console.log("pair plot", response.data);
        // setData(response.data);
        // const keys = [];
        // Object.keys(response.data.dynamic_features).map((key, i) =>
        //   keys.push(key)
        // );
        // setdyanmickeys(keys);
        setLoading((e) => !e);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    getData();
    //console.log(loading);
   // console.log(dynamickeys);
  }, []);

  const CreateNewSmartButton = (event) => {
    event.preventDefault();
    const req = {};
    const val1 = document.getElementById("one");
    const val2 = document.getElementById("two");
    var prod = data.configparams.product_id;
    var firstNumber = prod.match(/[0-9]+/);
    var num = prod.indexOf(firstNumber);
    if (num != -1) {
      prod = prod.substring(0, num);
    }
    console.log("nnn", prod);
    req.configparams = data.configparams;
    req.configparams.product_id = prod;
    console.log("rrrrrr", req);
    req.dynamic_features = [];
    if (val1.checked == true) {
      // ((req.dynamic_features[0].post_activate_code)).push(data.dynamic_features[0].post_activate_code)
      req.dynamic_features.push(data.dynamic_features[0][0]);
    } else {
      console.log("oops");
    }

    if (val2.checked == true) {
      //(req.dynamic_features[0].pre_posting_code).push(data.dynamic_features[0].pre_posting_code)
      req.dynamic_features.push(data.dynamic_features[1][0]);
    } else {
      console.log("oops");
    }
    console.log("rrrr", req);
    glassclassificationservices
      .getPairPlots(req)
      .then((response) => {
        console.log(response.data);
        alert("Congratulations" + response.data);
      })
      .catch((error) => {
        console.log(error);
        alert("Oops we are unable to create the product");
      });
  };

  return (
    <>
      <Navbar2 />
      <FeaturesSec>
        {loading ? (
          <div
            style={{
              margin: "auto",
              top: "50%",
              display: "block",
            }}
          >
            <Box sx={{ display: "flex" }}>
              <CircularProgress />
            </Box>
          </div>
        ) : (
          <FeaturesContainer>
            <FeaturesColumn>
              <FeaturesRow>
                <TopLine lightTopLine={lightTopLine}>
                  Smart Contract - NEW
                  <p
                    style={{ color: "grey", fontSize: "2vh", marginTop: "2vh" }}
                  >
                    Parameters based on New Product
                  </p>
                </TopLine>
              </FeaturesRow>
              <FeaturesRow>
                {" "}
                <TopLine
                  style={{
                    color: "whitesmoke",
                    fontSize: "3vh",
                    marginTop: "4vh",
                    marginBottom: "1vh",
                  }}
                  lightTopLine={lightTopLine}
                >
                  {" "}
                  Type of Product{" "}
                  <p
                    style={{
                      color: "grey",
                      fontSize: "1.5vh",
                      marginTop: "1vh",
                    }}
                  >
                    {" "}
                    Parameters are loaded based on product type *{" "}
                  </p>{" "}
                  <select id="dropdown" class="select-box">
                    {" "}
                    <optgroup label="Asset">{" "}
                    <option value="1">Loan</option>{" "}
                    <option value="2">Credit Card</option>{" "}:
                    </optgroup>{" "}
                    <optgroup label="Liability">{" "}
                    <option value="3">Savings</option>{" "}
                    <option value="4">Checking</option>{" "}
                    <option value="5">Current</option>{" "}
                    </optgroup>{" "}
                  </select>{" "}
                </TopLine>{" "}
              </FeaturesRow>
              <FeaturesRow>
                <TopLine
                  style={{
                    color: "whitesmoke",
                    fontSize: "3vh",
                    marginTop: "4vh",
                    marginBottom: "0vh",
                  }}
                  lightTopLine={lightTopLine}
                >
                  {" "}
                  Hooks{" "}
                  {/* <p style={{ color: "grey", fontSize: "1.5vh", marginTop: "1vh", }} > Parameters are loaded based on product type * </p> */}{" "}
                </TopLine>
                <TextWrapper>
                  <Collapse style={{ width: "40vw" }} accordion={true}>
                    <Panel
                      style={{ background: "#F5F5F5" }}
                      header="post_activate_code"
                      headerClass="my-header-class"
                    >
                      <FeatureWrap>
                        <h1 style={{ color: "F5F5F5", paddingBottom: "2vh" }}>
                          FundTransferFeature{" "}
                          <input
                            type="checkbox"
                            id="one"
                            name="one"
                            value="fund"
                          ></input>
                        </h1>

                        <Table>
                          <tbody>
                            <tr>
                              <td
                                style={{
                                  color: "white",
                                  padding: "1vh",
                                  fontSize: "2.5vh",
                                }}
                              >
                                <label>
                                  <b>
                                    {
                                      data.dynamic_features[0][0]
                                        .post_activate_code[0]
                                        .FundTransferFeature.params[0].name
                                    }
                                    :
                                  </b>
                                </label>
                              </td>
                              <td>
                                <input
                                  style={{
                                    borderRadius: "6px",
                                    textAlign: "center",
                                    padding: "0.7vh",
                                    marginLeft: "1vh",
                                  }}
                                  type="text"
                                  placeholder="Enter Value"
                                  name="denomination"
                                  defaultValue={
                                    data.dynamic_features[0][0]
                                      .post_activate_code[0].FundTransferFeature
                                      .params[0].default_value
                                  }
                                  required
                                />
                                <br />
                              </td>
                            </tr>
                          </tbody>
                        </Table>
                      </FeatureWrap>
                    </Panel>
                    <Panel
                      style={{ background: "#F5F5F5" }}
                      header="pre_posting_code"
                      headerClass="my-header-class"
                    >
                      <FeatureWrap>
                        <h1
                          style={{
                            color: "F5F5F5",
                            paddingBottom: "2vh",
                            marginLeft: "0%",
                          }}
                        >
                          DenominationCheckFeature{" "}
                          <input
                            type="checkbox"
                            id="two"
                            name="two"
                            value="denom"
                          ></input>
                        </h1>
                        <Table>
                          <tbody>
                            <tr>
                              <td
                                style={{
                                  color: "white",
                                  padding: "1vh",
                                  fontSize: "2.5vh",
                                }}
                              >
                                <label>
                                  <b>
                                    {
                                      data.dynamic_features[1][0]
                                        .pre_posting_code[0]
                                        .DenominationCheckFeature.params[0].name
                                    }
                                    :
                                  </b>
                                </label>
                              </td>
                              <td>
                                <input
                                  style={{
                                    borderRadius: "6px",
                                    textAlign: "center",
                                    padding: "0.7vh",
                                    marginLeft: "1vh",
                                  }}
                                  type="text"
                                  placeholder="Enter Value"
                                  name="denomination"
                                  defaultValue={
                                    data.dynamic_features[1][0]
                                      .pre_posting_code[0]
                                      .DenominationCheckFeature.params[0]
                                      .default_value
                                  }
                                  required
                                />
                                <br />
                              </td>
                            </tr>
                          </tbody>
                        </Table>
                      </FeatureWrap>
                    </Panel>
                  </Collapse>
                </TextWrapper>
                <HeroBtnWrapper style={{ display: "inline-block" }}>
                  <Button
                    onClick={(event) => CreateNewSmartButton(event)}
                    onMouseEnter={onHover}
                    onMouseLeave={onHover}
                    primary="true"
                    dark="true"
                  >
                    Create Contract {hover ? <ArrowForward /> : <ArrowRight />}
                  </Button>
                </HeroBtnWrapper>
              </FeaturesRow>
            </FeaturesColumn>
            <FeaturesColumn>
              <ImgWrapper>
                <Img src={TM} alt="car" />
              </ImgWrapper>
            </FeaturesColumn>
          </FeaturesContainer>
        )}
      </FeaturesSec>
    </>
  );
};

export default CreateNewContract;
