import React, { useState } from "react";
import Navbar2 from "../../components/Navbar/navbar2";
import TM from "../../common/assets/form.svg";
import "./Features.css";
import { useEffect } from "react";
import { useParams } from "react-router-dom";
import {fetchBoxPlots} from "../../services/glassclassificationservices"
import {
  HeroBtnWrapper,
  ArrowForward,
  ArrowRight,
} from "../../components/HeroSection/HeroElements";
import { Button } from "../../components/ButtonElement";
import CircularProgress from "@mui/material/CircularProgress";
import Box from "@mui/material/Box";
import {
  FeaturesRow,
  FeaturesColumn,
  TextWrapper,
  Img,
  TopLine,
  FeaturesSec,
  FeaturesContainer,
  ImgWrapper,
  Table,
  FeatureWrap,
} from "./Features.elements";
import Collapse from "rc-collapse";
var Panel = Collapse.Panel;
require("rc-collapse/assets/index.css");

const TextClassification = ({ lightTopLine }) => {
  const [text, setText] = useState('');
  const [classification, setClassification] = useState("");

  const handleSubmit = async (e) => {
      e.preventDefault();
      try {
          const response = await fetch('http://localhost:5000/classify-text', {
              method: 'POST',
              headers: {'Content-Type': 'application/json'},
              body: JSON.stringify({ description: text })
          });

          const result = await response.json();
          setClassification(result.class);
      } catch (error) {
          console.error('Error:', error);
          alert('Failed to classify the text.');
      }
  };

  return (
    <>
      <Navbar2 />
      <FeaturesSec>
      <h1>Glass Type Classification from Images</h1>
      <div>
        <HeroBtnWrapper>
        <input type="file" onChange={handleImageChange} accept="image/*" className="upload-input"/>
        </HeroBtnWrapper>
            {imageUrl && <img src={imageUrl} alt="Uploaded"  className="uploaded-image" />}
            <br>
            </br>
            {classification && <p className="classification-result">Glass Type Classification: {classification}</p>}
        </div>

    </FeaturesSec>
    </>
  );
};

export default TextClassification;
