import { useNavigate } from "react-router-dom";
import React, { Component } from "react";
import {
  ProductsContainer,
  ProductsWrapper,
  ProductsCard,
  ProductsIcon,
  ProductsH1,
  ProductsH2,
  ProductsP,
} from "./ProductElement";

import DashboardServices from '../../services/DashboardServices'

function NewProduct() {
    const navigate = useNavigate();
    const [categoryData, setCategoryData] = useState([]);
    const [loading, setLoading] = useState(true)
    const getData = () => {
      DashboardServices.getAllCategories().then(response => {
          console.log(response.data)
          const modifiedData = response.data.map(item => ({
              productId: item.categoryId,
              productName: item.categoryName,
              productImage: generateImage(item.categoryImage),
              productDescription: item.description
          }));
          setCategoryData(modifiedData);
          setLoading(e => !e)
      }).catch(error => {
          console.log(error)
      })
  }
  const  generateImage =(base64Image)=>{
    const imageData = base64Image.split(",")[1];
const decodedImage = atob(imageData);
const arrayBuffer = new Uint8Array(decodedImage.length);
for (let i = 0; i < decodedImage.length; i++) {
arrayBuffer[i] = decodedImage.charCodeAt(i);
}
const blob = new Blob([arrayBuffer], { type: 'image/jpeg' });
return URL.createObjectURL(blob);
}

useEffect(() => {
    getData();
}, []);

    const handleClick = (event, param) => {
      console.log("dddddddd", event)
      console.log("pppp", param)
      navigate(`/createcontract/${param}`);
  };

  return (
    <>
      <ProductsContainer id="newproduct" style={{background: "#f9f9f9", display: "flow-npm root"}}>
        <ProductsH1 style={{color: "#010606"}}>FEATURED CATEGORIES</ProductsH1>
        <ProductsWrapper style={{gridTemplateColumns: "1fr 1fr"}}>
          <ProductsCard onClick={(event) => handleClick(event,'i')}>
            {/* <ProductsIcon src={Icon1}/> */}
            <ProductsH2>Asset</ProductsH2>
            <ProductsP>Apply for credit at the comfort of your home.</ProductsP>
          </ProductsCard>
          <ProductsCard onClick={(event) => handleClick(event,'j')}>
          {/* <ProductsIcon src={Icon2}/> */}
            <ProductsH2>Liability</ProductsH2>
            <ProductsP>
              Access our platform online anywhere in the world.
            </ProductsP>
          </ProductsCard>
          <ProductsCard onClick={(event) => handleClick(event,'i')}>
            {/* <ProductsIcon src={Icon1}/> */}
            <ProductsH2>Asset</ProductsH2>
            <ProductsP>Apply for credit at the comfort of your home.</ProductsP>
          </ProductsCard>
        </ProductsWrapper>
      </ProductsContainer>
    </>
  );
}

export default NewProduct;
