// LoginScreen.js

import React, { useState } from 'react';
import './LoginScreen.css'; // Importing the CSS file
import { Container, Form, Button, Row, Col, Modal } from 'react-bootstrap';
import { login } from "../../services/LoginServices"
const LoginComponent = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showOtpModal, setShowOtpModal] = useState(false);
  const handleLogin = async() => {
    try{
        const data = await login(username,password);
        console.log("data from services",data)
        setIsLoggedIn(true);
        setShowOtpModal(true);
    }
    catch (error) {
        console.error('Error in handleClick:', error);
      }

  };
  const handleOtpSubmit = () => {
    // Logic to handle OTP submission
    setShowOtpModal(false);
    // Further processing after OTP verification
  };
    return (
        <Container className="d-flex align-items-center justify-content-center " style={{ minHeight: "100vh" }}>
        <div className="w-100" style={{ maxWidth: "400px" }}>
          <Form>
            <h2 className="text-center mb-4">Login</h2>
            <Form.Group controlId="formBasicEmail">
              <Form.Label>Username</Form.Label>
              <Form.Control type="text" placeholder="Enter username" 
              value ={username}
                onChange={(e) => setUsername(e.target.value)}/>
            </Form.Group>

            <Form.Group controlId="formBasicPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" placeholder="Password"
              value ={password}
              onChange={(e) => setPassword(e.target.value)}/> 
            </Form.Group>

            <Button variant="primary" type="submit" className="w-100 mt-4">
              Login
            </Button>

            <div className="w-100 text-center mt-3">
              <a href="#">Forgot Password?</a>
            </div>
          </Form>

          <Row className="py-4">
            <Col>
              <div className="w-100 text-center">
                Need an account? <a href="#">Sign Up</a>
              </div>
            </Col>
          </Row>
        </div>
        <Modal show={showOtpModal} onHide={() => setShowOtpModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Enter OTP</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label>OTP</Form.Label>
              <Form.Control type="text" placeholder="4-digit OTP" />
            </Form.Group>
            <Button variant="primary" type="button" onClick={handleOtpSubmit}>
              Submit
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
      </Container>
    );
};

export default LoginComponent;
