import axiosInstance from "../Auth/axiosInstance"
class SmarContractServices {
    getAllProducts() {
        try {
             return axiosInstance.get(`${process.env.REACT_APP_ZENOO_URL}/capgemini/api/smartcontract/getallproducts`)
           // return axiosInstance.get("http://tmsmu-2066051547.us-east-1.elb.amazonaws.com/tm/products")
           //return axiosInstance.get("http://localhost:5000/tm/products")
        }
        catch (err) {
            console.log(err)
        }
    }
    getAllParameters(product_id) {
        try {
             return axiosInstance.get(`${process.env.REACT_APP_ZENOO_URL}/capgemini/api/smartcontract/getallparameters/${product_id}`)
             //return axiosInstance.get(`http://localhost:5000/tm/getParams/${product_id}/`)
        }
        catch (err) {
            console.log(err)
        }

    }

    createSmartContract(main,product_id){
        try {
            return axiosInstance.post(`${process.env.REACT_APP_ZENOO_URL}/capgemini/api/smartcontract/updateproduct/${product_id}`, main)
            //return axiosInstance.post(`http://localhost:5000/tm/updateParams/${product_id}`,main)
        }
        catch (err) {
            console.log(err)
        }
    }


    getdynamicsavings(){
        try {
            //return axiosInstance.get(`${process.env.REACT_APP_ZENOO_URL}/capgemini/api/smartcontract/dynamicsavings`)
            //return axiosInstance.get("http://localhost:5000/tm/dynamicsavings")
            return axiosInstance.get("http://tmsmu-2066051547.us-east-1.elb.amazonaws.com/tm/dynamicsavings")
        }
        catch (err) {
            console.log(err)
        }
    }

    createnewsmartcontract(payload)
    {
        try {
           // return axiosInstance.post(`${process.env.REACT_APP_ZENOO_URL}/capgemini/api/smartcontract/dynamicproduct`,payload)
           // return axiosInstance.post(`http://localhost:5000/tm/updateParams/${product_id}`,main)
            return axiosInstance.post("http://tmsmu-2066051547.us-east-1.elb.amazonaws.com/tm/dynamicProduct/cgsavingaccountsmu",payload)
        }
        catch (err) {
            console.log(err)
        }
    }
    uploadfile(url,formData,config)
    {
        try {
           // return axiosInstance.post(`${process.env.REACT_APP_ZENOO_URL}/capgemini/api/smartcontract/dynamicproduct`,payload)
           return axiosInstance.post(url,formData,config)
           // return axiosInstance.post("http://tmsmu-2066051547.us-east-1.elb.amazonaws.com/tm/dynamicProduct/cgsavingaccountsmu",payload)
        }
        catch (err) {
            console.log(err)
        }
    }

}
export default new SmarContractServices();
