import React, { useEffect } from "react";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import smartContractServices from '../../services/smartcontractservices';
import Carousel from "react-elastic-carousel"
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import ProductIcon from '../../common/assets/productsIcon.svg';
import {
  ProductsContainer,
  ProductsWrapper,
  ProductsWrapperCar,
  ProductsContainerCar,
  ProductsCard,
  ProductsIcon,
  ProductsH1,
  ProductsH2,
  ProductsP,
} from "./ProductElements";
import { height } from "@mui/system";


function Products() {
  const navigate = useNavigate();
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true)
  const getData = () => {
    // fetch("https://api.adviceslip.com/advice")
    //     .then((response) => response.json())
    //     .then((data) => {
    //         setData(data.slip.advice);
    //     });
    smartContractServices.getAllProducts().then(response => {
      console.log(response.data)
      setData(response.data)
      setLoading(e => !e)
    }).catch(error => {
      console.log(error)
    })

  }

  useEffect(() => {
    getData();
  }, []);


  window.addEventListener("beforeunload", (event) => {
    getData();
    console.log("API call before page reload");
  });

  window.addEventListener("unload", (event) => {
    getData();
    console.log("API call after page reload");
  });
  const handleClick = (event, param) => {
    console.log("dddddddd", event)
    console.log("pppp", param)
    navigate(`/createcontract/${param}`);
  };


  return (
    <>
      <ProductsContainer id="products">
        <ProductsH1>Create New Product Version</ProductsH1>
        {loading
          ? <div style={{
            margin: "auto",
            top: "50%",
            display: "block",
          }}>
            <Box sx={{ display: 'flex' }}>
              <CircularProgress />
            </Box>
          </div>
          :
          <ProductsContainerCar id="products">
            <Carousel style={{ height: "100%" }} itemsToShow={3}>
              {
                data.map((pe, i) => {
                  return (
                    <ProductsCard onClick={event => handleClick(event, pe.id)}>
                      <ProductsIcon src={ProductIcon} />
                      <ProductsH2>{pe.display_name}</ProductsH2>
                      <ProductsP>Apply for credit at the comfort of your home.</ProductsP>
                    </ProductsCard>
                  );
                })
              }
            </Carousel>
          </ProductsContainerCar>
        }
      </ProductsContainer>
    </>
  );
};

export default Products;
