import React, { useState } from "react";
import Navbar2 from "../../components/Navbar/navbar2";
import TM from "../../common/assets/form.svg";
import "./Features.css";
import { useEffect } from "react";
import { useParams } from "react-router-dom";
import {fetchBoxPlots} from "../../services/glassclassificationservices"
import {
  HeroBtnWrapper,
  ArrowForward,
  ArrowRight,
} from "../../components/HeroSection/HeroElements";
import { Button } from "../../components/ButtonElement";
import CircularProgress from "@mui/material/CircularProgress";
import Box from "@mui/material/Box";
import {
  FeaturesRow,
  FeaturesColumn,
  TextWrapper,
  Img,
  TopLine,
  FeaturesSec,
  FeaturesContainer,
  ImgWrapper,
  Table,
  FeatureWrap,
} from "./Features.elements";
import Collapse from "rc-collapse";
var Panel = Collapse.Panel;
require("rc-collapse/assets/index.css");

const ImageClassification = ({ lightTopLine }) => {
  const [image, setImage] = useState(null);
  const [classification, setClassification] = useState("");

  const handleImageChange = (e) => {
      const file = e.target.files[0];
      setImage(file);
      classifyImage(file);
  };

  const classifyImage = async (image) => {
    const formData = new FormData();
    formData.append('file', image);

    try {
        const response = await fetch('http://localhost:8001/ml/classify', {
            method: 'POST',
            body: formData,
        });

        const result = await response.json();
        setClassification(result.class);
    } catch (error) {
        console.error('Error:', error);
        alert('Failed to classify the image.');
    }
};

 


  return (
    <>
      <Navbar2 />
      <FeaturesSec>
      <h1>Glass Type Classification from Images</h1>
      <div>
            <input type="file" onChange={handleImageChange} accept="image/*" />
            {classification && <p>Classification: {classification}</p>}
        </div>

    </FeaturesSec>
    </>
  );
};

export default ImageClassification;
