import { useNavigate } from "react-router-dom";
import React, { Component } from "react";
import { useState,useEffect } from "react";
import Carousel from "react-elastic-carousel"
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import {
  ProductsContainer,
  ProductsWrapper,
  ProductsContainerCar,
  ProductsCard,
  ProductsIcon,
  ProductsH1,
  ProductsH2,
  categoryH1,
  ProductsP,
} from "./ProductElement";

import DashboardServices from '../../services/DashboardServices'

function NewProduct() {
    const navigate = useNavigate();
    const [categoryData, setCategoryData] = useState([]);
    const [loading, setLoading] = useState(true)
    const getData = () => {
      DashboardServices.getAllCategories().then(response => {
          console.log(response.data)
          const modifiedData = response.data.map(item => ({
              categoryId: item.categoryId,
              categoryName: item.categoryName,
              categoryImage: generateImage(item.categoryImage),
              categoryDescription: item.description
          }));
          setCategoryData(modifiedData);
          setLoading(e => !e)
      }).catch(error => {
          console.log(error)
      })
  }
  const  generateImage =(base64Image)=>{
    const imageData = base64Image.split(",")[1];
const decodedImage = atob(imageData);
const arrayBuffer = new Uint8Array(decodedImage.length);
for (let i = 0; i < decodedImage.length; i++) {
arrayBuffer[i] = decodedImage.charCodeAt(i);
}
const blob = new Blob([arrayBuffer], { type: 'image/jpeg' });
return URL.createObjectURL(blob);
}

useEffect(() => {
    getData();
}, []);

    const handleClick = (event, param) => {
      console.log("dddddddd", event)
      console.log("pppp", param)
      navigate(`/createcontract/${param}`);
  };

  return (
    <>
   <ProductsContainer id="categories" style={{background: "#f9f9f9", display: "flow-npm root"}}>
                <categoryH1>FEATURED CATEGORIES </categoryH1>
                {loading
                    ? <div style={{
                        margin: "auto",
                        top: "50%",
                        display: "block",
                    }}>
                        <Box sx={{ display: 'flex' }}>
                            <CircularProgress />
                        </Box>
                    </div>
                    :
                        <Carousel style={{ height: "100%" }} itemsToShow={3}>
                            {
                                categoryData.map((ce, i) => {
                                    return (
                                        <ProductsCard onClick={event => handleClick(event, ce.categoryId)}>
                                            <ProductsIcon src={ce.categoryImage} />
                                            <ProductsH2>{ce.categoryName}</ProductsH2>
                                            <ProductsP>{ce.categoryDescription}</ProductsP>
                                        </ProductsCard>
                                    );
                                })
                            }
                        </Carousel>

                }
            </ProductsContainer>
    </>
  );
}

export default NewProduct;
